  private function inputs_kern($start=-0.3,$trichqy=0.0500001,$count_of_select=26){
    $array_of_kern=array();
    for($i=0;$i<$count_of_select;$i++){
      $array_of_kern[number_format($start,2).'em']=number_format($start,2).'em';
      $start=$start+$trichqy;
    }
    return $array_of_kern;
  }
