             */
            function checkBrowserShiretoko() {
                if ( stripos( $this->_agent, 'Mozilla' ) !== false && preg_match( '/Shiretoko\/([^ ]*)/i', $this->_agent, $matches ) ) {
                    $this->setVersion( $matches[1] );
